CREATE TABLE buildings (id INTEGER PRIMARY KEY, zone TEXT, p_lat NUMERIC, p_lon NUMERIC, number NUMERIC, label TEXT, keywords TEXT);
CREATE TABLE rooms (id INTEGER PRIMARY KEY, p_lat NUMERIC, p_lon NUMERIC, building_id NUMERIC, floor NUMERIC, type NUMERIC, label TEXT);
CREATE TABLE services (id INTEGER PRIMARY KEY, p_lat NUMERIC, p_lon NUMERIC, description TEXT, building_id NUMERIC, floor NUMERIC);
